const long unsigned int OK = 0x8000000000000000;

const int256 r1 = {0x30644E72E131A029,0xB85045B68181585D,0x2833E84879B97091,0x43E1F593F0000001};


Fp12 Millerloop(G1 P, G2 Q)
{	
	int i = 253;
	int256 r = r1;
	
	
	r = shift_left_256(shift_left_256(shift_left_256(r)));
	
	G1 T = P;
	
	Fp12 f = Fp12_one();

	while (i)
	{	
		if (G1_equal(G1_add(T,T),G1_zero()))
		{
			f = Fp12_mul_by_Fp6(f,Fp6xFp_add(Q.x,Fp_opp(T.x)));
		}
		else {
		Fp m = Fp_div(Fp_mul(Fp_from_int(3),Fp_mul(T.x,T.x)) , Fp_mul(Fp_from_int(2),T.y));
		Fp12 l = Fp12xFp6_add(Q.y , Fp6xFp_add(Fp6_mul_by_scalar(Fp6xFp_add(Q.x,Fp_opp(T.x)), Fp_opp(m)),Fp_opp(T.y)));
		f = Fp12_mul(Fp12_mul(f,f), l);
		T = G1_add(T,T);
		}

		if ((r.u3 & OK) != 0){
		
		if (G1_equal(G1_add(T,P),G1_zero()))
		{
			f = Fp12_mul_by_Fp6(f,Fp6xFp_add(Q.x,Fp_opp(T.x)));
			printf("la vie est belle wesh \n" ); printf("%d\n",i );
		}
		else {
			Fp m1 = Fp_div(Fp_sub(T.y,P.y),Fp_sub(T.x, P.x));
			Fp12 l = Fp12xFp6_add(Q.y,Fp6xFp_add(Fp6_mul_by_scalar(Fp6xFp_add(Q.x,Fp_opp(T.x)), Fp_opp(m1)),Fp_opp(T.y)));
			f = Fp12_mul(f,l);
			T = G1_add(T,P); }
		}
		r = shift_left_256(r);
		i = i-1;
		
	}
	return f; 
}


